package taller2_6_a;


class taller2_6_a {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		
	    int n1=1;
	    int nn=100;
	    int n2;
	    
		for(n2 = 1; n2 <= nn; n2++) {
			if(n2 <= nn) {
				System.out.println(+ n2 +"+");
				
				
			}else {
				System.out.println(+ nn);
			}
			
			n1 = n2 + n1;
			
			if(n2 == nn) {
				System.out.println("="+ n1);
			}
		}
		
		
	}

}
